// PasswordComponent.tsx
import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

interface PasswordComponentProps {
  onEncrypt: (encryptedText: string) => void;
  onDecrypt: (decryptedText: string) => void;
  encryptedText: string;  // Ajout de la prop encryptedText
}

const PasswordComponent: React.FC<PasswordComponentProps> = ({ onEncrypt, onDecrypt, encryptedText }) => {
  const [password, setPassword] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const encryptMessage = () => {
    if (!password || !message) {
      alert('Mot de passe et message sont requis');
      return;
    }
    const encrypted = CryptoJS.AES.encrypt(message, password).toString();
    onEncrypt(encrypted);
  };

  const decryptMessage = () => {
    if (!password || !encryptedText) {
      alert('Mot de passe et texte crypté sont requis');
      return;
    }
    const bytes = CryptoJS.AES.decrypt(encryptedText, password);
    const decrypted = bytes.toString(CryptoJS.enc.Utf8);
    onDecrypt(decrypted);
  };

  return (
    <div>
      <h3>Entrer un message à crypter</h3>

      <textarea
        placeholder="Écrivez votre message ici"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />

      <input
        type="password"
        placeholder="Entrez un mot de passe"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <button onClick={encryptMessage}>Crypter</button>
      <button onClick={decryptMessage}>Décrypter</button>
    </div>
  );
};

export default PasswordComponent;
