import React, { createContext, useState, useContext, ReactNode } from 'react';

interface Entry {
  id: string;
  title: string;
  content: string;
  encrypted: boolean;
}

interface JournalContextProps {
  entries: Entry[];
  addEntry: (entry: Entry) => void;
  updateEntry: (id: string, updatedEntry: Entry) => void;
  deleteEntry: (id: string) => void;
}

const JournalContext = createContext<JournalContextProps | undefined>(undefined);

export const useJournal = (): JournalContextProps => {
  const context = useContext(JournalContext);
  if (!context) throw new Error('useJournal must be used within a JournalProvider');
  return context;
};

interface JournalProviderProps {
  children: ReactNode;
}

export const JournalProvider: React.FC<JournalProviderProps> = ({ children }) => {
  const [entries, setEntries] = useState<Entry[]>([]);

  const addEntry = (entry: Entry) => setEntries([...entries, entry]);
  const updateEntry = (id: string, updatedEntry: Entry) => {
    setEntries(entries.map(entry => (entry.id === id ? updatedEntry : entry)));
  };
  const deleteEntry = (id: string) => {
    setEntries(entries.filter(entry => entry.id !== id));
  };

  return (
    <JournalContext.Provider value={{ entries, addEntry, updateEntry, deleteEntry }}>
      {children}
    </JournalContext.Provider>
  );
};
