const App: React.FC = () => {
  const [encryptedText, setEncryptedText] = useState<string>(''); // Stocke le texte crypté

  // Fonction de rappel pour recevoir le texte crypté
  const handleEncryptedText = (text: string) => {
    setEncryptedText(text); // Met à jour l'état avec le texte crypté
  };

  return (
    <div className="App">
      <h1>Encrypted Diary</h1>

      {/* Affichage du composant de cryptage */}
      <PasswordComponent onEncrypt={handleEncryptedText} onDecrypt={function (decryptedText: string): void {
        throw new Error('Function not implemented.');
      } } encryptedText={''} />

      {/* Si le texte crypté existe, l'afficher */}
      {encryptedText && (
        <div>
          <h2>Message Crypté :</h2>
          <p>{encryptedText}</p>
        </div>
      )}
    </div>
  );
};

export default App;
