import React from "react";
import { JournalProvider } from "./JournalContext";
import NewEntry from "./components/NewEntry";
import EntryList from "./components/EntryList";
import PasswordComponent from "./components/PasswordComponent";
import "./App.css";

function App() {
  return (
    <JournalProvider> {/* Envelopper l'application avec le Provider du contexte */}
      <div>
        <h1>Mon Journal Crypté</h1>

        {/* Formulaire pour ajouter une nouvelle entrée */}
        <NewEntry />

        {/* Liste des entrées existantes */}
        <EntryList />

        {/* Composant pour décryptage */}
        {/* Remplacer par un texte crypté */}
        <PasswordComponent
          onDecrypt={(decryptedText) => {
            console.log("Texte décrypté:", decryptedText);
          }}
          encryptedText="TonTexteCryptéIci" 
        />
      </div>
    </JournalProvider>
  );
}

export default App;
