import React, { createContext, useState, useContext, ReactNode } from 'react';

interface Entry {
  id: string;
  title: string;
  content: string;
  encrypted: boolean;
}

interface JournalContextProps {
  entries: Entry[];
  addEntry: (entry: Entry) => void;
  updateEntry: (id: string, updatedEntry: Entry) => void;
  deleteEntry: (id: string) => void;
}

const DiaryContext = createContext<JournalContextProps | undefined>(undefined);

export const useDiary = (): JournalContextProps => {
  const context = useContext(DiaryContext);
  if (!context) throw new Error('useDiary must be used within a DiaryProvider');
  return context;
};

interface JournalContextProviderProps {
  children: ReactNode;
}

export const DiaryProvider: React.FC<JournalContextProviderProps> = ({ children }) => {
  const [entries, setEntries] = useState<Entry[]>([]);

  const addEntry = (entry: Entry) => setEntries([...entries, entry]);

  const updateEntry = (id: string, updatedEntry: Entry) => {
    setEntries(entries.map(entry => entry.id === id ? updatedEntry : entry));
  };

  const deleteEntry = (id: string) => {
    setEntries(entries.filter(entry => entry.id !== id));
  };

  return (
    <DiaryContext.Provider value={{ entries, addEntry, updateEntry, deleteEntry }}>
      {children}
    </DiaryContext.Provider>
  );
};
