import React, { useState, useContext } from "react";
import { JournalContext } from "../JournalContext";  // Assure-toi que le chemin est correct

const NewEntry: React.FC = () => {
  const [text, setText] = useState<string>("");
  const { addEntry } = useContext(JournalContext);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (text) {
      const id = Date.now().toString();  // Génère un identifiant unique basé sur le timestamp
      addEntry(text, id);  // Appelle addEntry avec le texte et l'id
      setText("");  // Réinitialiser le champ de texte après l'envoi
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Écrire une nouvelle entrée"
      />
      <button type="submit">Ajouter</button>
    </form>
  );
};

export default NewEntry;
